mapper.update:
update dish
name = #{name}, category_id = #{categoryId}, price = #{price}, status = #{status}, update_time = #{updateTime}, update_user = #{updateUser}
where id = #{id}

mapper.delete:
delete from dish
where id in
#{id}

delete from dish_flavor
where dish_id in
#{id}

mapper.select:
select * from dish


        name like concat('%', #{name}, '%')


        AND category_id = #{categoryId}


        AND status = #{status}


order by update_time desc

select name
from category
where id = #{categoryId}
